<?php    include_once("common.php");    if (isauth())    {        if (isset($_REQUEST['truckid']) && ($_REQUEST['truckid'] != ''))        {            $mysql = get_connection();            $stmt = $mysql->prepare($sql['daily_offer:insert']);            $stmt->bind_params($_REQUEST['truckid']);            if($stmt->execute())            {                if (isset($_REQUEST['menu']))                    redirect_in_site('sys/admin_aktualis_truck_menu.php?result=100');                else                    redirect_in_site('?page=admin_select_daily_special');            }        }    }?>