<?php  /**   *  This file displays the flyer page editor page of the admin section.   *   *  Language is hardcoded Hungarian, others are not required.   */  if(!loggedin() || !isauth())  {    redirect_in_site("?page=$default_page&lang=$lang");  }  else  {      if (isset($_REQUEST['pageid']))    {      $flyer = $_SESSION['flyers'][$_REQUEST['pageid']];    }    else    {          }            /// Start outputting the page.    $body_onload_functions .= " initialize_editor(14); ";        $template = new Template();        $variables = array ("FLYER" => get_flyer_table($flyer, true),                        "TRUCKS" => list_flyer_trucks(),                        "PAGEID" => $_REQUEST['pageid'],                        "FLYERID" => (floor($_REQUEST['pageid']/2) + 1) . '. oldal ' . (($_REQUEST['pageid'] % 2 == 1) ? 'hátulja' : 'eleje'));    $template->assign_var_array($variables);        $main_content = $template->compile("sys/lang/hun/flyer_editor_0");  //WARNING: Language hardcoded, no other languages needed        include("sys/tpl/main.tpl");  }?>