<?phpob_start();include_once("common.php");if (isauth()) {		$mysql = get_connection();	$mysql->execute($sql['setutf']);		function ListIspartOptions($cur) {		global $language;		$out = '<option value="" betu=""> - - - </option>';		foreach($language['aktualis:ispart-sort'] AS $key) {			$row = $language['aktualis:ispart'][$key];			$out .= '<option value="'.($row['ID']).'" betu="'.($row['chars']).'" '.(($row['ID']==$cur)?('SELECTED'):('')).'>'.($row['value']).'</option>';		}		return $out;	}		function ListTypeOptions($cur) {		global $mysql;		$out = '<option value=""> - - - </option>';		$stmt = $mysql->prepare('			SELECT * FROM `truck_type_hun` ORDER BY `ispart`, IF(`value` LIKE "%n/a%",0,1), `value`		');		$stmt->execute();		$typelist = $stmt->fetch_all();		foreach ($typelist as $type) {			$out .= '<option value="'.($type['ID']).'" class="ispart-'.($type['ispart']).'" '.(($type['ID']==$cur)?('SELECTED'):('')).'>'.($type['value']).'</option>';		}		return $out;	}		print('		<h1>Termékek feltöltése!</h1>		<form action="" method="post">			<div>				<label>Saxon szám:</label>				<select name="char" id="ispart_betu" style="width:35px;" required>'.(($_REQUEST['char'])?('<option value="'.($_REQUEST['char']).'">'.($_REQUEST['char']).'</option>'):('<option value="">-</option>')).'</select> -				<input type="text" name="start" value="'.($_REQUEST['start']).'" required placeholder="1000" style="width:65px;" /> -tól,				<input type="text" name="end" value="'.($_REQUEST['end']).'" required placeholder="9999" style="width:65px;" /> -ig.			</div>			<div>				<label>Kategória:</label>				<select name="ispart" onchange="ispComboToSaxonChar(this);" style="width:120px" required>					'.(ListIspartOptions($_REQUEST['ispart'])).'				</select>			</div>			<div>				<label>Fajta:</label>				<select name="type" class="newtypes" style="width:120px" required>					'.(ListTypeOptions($_REQUEST['type'])).'				</select>			</div>			<div>				<label>&nbsp;</label>				<button type="submit">Feltölt!</button>			</div>		</form>		<style>			form > div { position: relative; display: block; padding: 3px; }			label { display: inline-block; min-width: 90px; text-align: right; font-weight: bold; }			.newtypes > option { display: none; }			.newtypes.ispart-0 > option.ispart-0,			.newtypes.ispart-1 > option.ispart-1,			.newtypes.ispart-2 > option.ispart-2,			.newtypes.ispart-3 > option.ispart-3,			.newtypes.ispart-4 > option.ispart-4,			.newtypes.ispart-5 > option.ispart-5,			.newtypes.ispart-6 > option.ispart-6,			.newtypes.ispart-7 > option.ispart-7,			.newtypes.ispart-8 > option.ispart-8,			.newtypes.ispart-9 > option.ispart-9,			.newtypes.ispart-10 > option.ispart-10,			.newtypes.ispart-11 > option.ispart-11,			.newtypes.ispart-12 > option.ispart-12,			.newtypes.ispart-13 > option.ispart-13,			.newtypes.ispart-14 > option.ispart-14,			.newtypes.ispart-15 > option.ispart-15,			.newtypes.ispart-16 > option.ispart-16,			.newtypes.ispart-17 > option.ispart-17,			.newtypes.ispart-18 > option.ispart-18,			.newtypes.ispart-19 > option.ispart-19			{ display: block; }		</style>	');	if (isset($_REQUEST['char']) AND $_REQUEST['char']	AND isset($_REQUEST['start']) AND $_REQUEST['start']	AND isset($_REQUEST['end']) AND $_REQUEST['end']) {		for($i = (int)$_REQUEST['start']; $i<=(int)$_REQUEST['end']; $i++) {			$char = strtoupper($_REQUEST['char']);			$num = str_repeat('0',(((4-strlen($i))>0)?(4-strlen($i)):(0))).$i;			$stmt = $mysql->prepare('SELECT `truck_id` FROM `trucks` WHERE `truck_saxon-id` = "'.($char).'-'.($num).'"');			if($stmt->execute()) {				if($stmt->num_rows()) {					print('<div><strong>Padding:</strong> '.($char).'-'.($num).'</div>');				} else {					print('<div><strong>Insert:</strong> '.($char).'-'.($num).'</div>');					$ins = $mysql->prepare('						INSERT INTO `trucks`							( `truck_saxon-id`, `truck_function`, `truck_type`, `truck_ispart`, `truck_state`, `truck_product_status`, `truck_depot`, `truck_date` )							VALUES							( "'.($char).'-'.($num).'", "1230", "'.($_REQUEST['type']).'", "'.($_REQUEST['ispart']).'", "A", "1", "10", "'.(date('Y-m-d')).'" )					');					$ins->execute();				}			}		}	}}	$main_content = ob_get_contents();ob_end_clean();include(realpath(dirname(__FILE__)).'/../tpl/main.tpl');?>