<?phpif (!loggedin() || !isauth()) {    redirect_in_site("?page=$default_page&lang=$lang");} else if (isset($_REQUEST['truckid'])) {    $template = new Template();    $list = "";    $mysql = get_connection();    $mysql->execute($sql['setutf']);    $stmt = $mysql->prepare($sql['truck_lesserdetails']);    $stmt->bind_params("hun", $_REQUEST['truckid']);    if ($stmt->execute()) {        $truck = $stmt->fetch_all();        $saxonid = $truck[0]['truck_saxon-id'];        $stmt = $mysql->prepare($sql['truck_getimages']);        $stmt->bind_params($_REQUEST['truckid']);        if ($stmt->execute()) {            $result = $stmt->fetch_all();            if ($stmt->num_rows() > 0) {                $list = '<table>';                foreach ($result as $image) {                    $list .= '<tr><td style="width:320px">';                    if (!is_file('img/trucks/' . $image['image_filename'])) {                        $list .= '<img src="../img/trucks/truck_notfound.jpg"/>' .                                '</td><td style="width:320px">A kép nem található a szerveren!<br />';                    } else {                        $list .= '<img src="img/trucks/' . $image['image_filename'] . '" style="width:320px; height:240px"/>' .                                '</td><td style="width:320px">' . $image['image_filename'] . '<br />';                    }                    if ($image['image_id'] != $truck[0]['truck_default-image']) {                        $list .= '  <a href="sys/truckman_setdefaultimage.php?truckid=' . $_REQUEST['truckid'] . '&imageid=' . $image['image_id'] . '">kép kiválasztása alapértelmezettként</a><br />';                    } else {                        $list .= 'ez az alapértelmezett kép<br />';                    }                    $list .= '<br />                    	<form action="sys/truckman_setimage_labels.php?truckid=' . $_REQUEST['truckid'] . '&imageuniqueid=' . $image['image_unique-id'] . '" method="post" style="margin:0px;padding:0px;display:block;">                    		<div>                    			Címke:                    			<input type="text" name="illustration" value="' . (($image['image_illustration'])?($image['image_illustration']):('')) . '" placeholder="" />                    			<button type="submit">»</button>                    		</div>                    	</form><br />';/*                    if ($image['image_illustration'] == 0) {                        $list .= '<br />  <a href="sys/truckman_setimage_labels.php?illustration=1&notworking=' . $image['image_notworking'] . '&truckid=' . $_REQUEST['truckid'] . '&imageuniqueid=' . $image['image_unique-id'] . '">Címke hozzáadása: illusztráció</a><br />';                    } else {                        $list .= '<br />  <a href="sys/truckman_setimage_labels.php?illustration=0&notworking=' . $image['image_notworking'] . '&truckid=' . $_REQUEST['truckid'] . '&imageuniqueid=' . $image['image_unique-id'] . '">Címke eltávolítása: illusztráció</a><br />';                    }                    if ($image['image_notworking'] == 0) {                        $list .= '<br />  <a href="sys/truckman_setimage_labels.php?illustration=' . $image['image_illustration'] . '&notworking=1&truckid=' . $_REQUEST['truckid'] . '&imageuniqueid=' . $image['image_unique-id'] . '">Címke hozzáadása: Nem működik</a><br />';                    } else {                        $list .= '<br />  <a href="sys/truckman_setimage_labels.php?illustration=' . $image['image_illustration'] . '&notworking=0&truckid=' . $_REQUEST['truckid'] . '&imageuniqueid=' . $image['image_unique-id'] . '">Címke eltávolítása: Nem működik</a><br />';                    }*/                    $list .= '<br />  <a href="sys/truckman_deleteimage.php?truckid=' . $_REQUEST['truckid'] . '&imageid=' . $image['image_id'] . '">kép törlése</a><br />';                    $list .= '</td></tr>';                }                $list .= '</table>';            }            else                $list = 'Ehhez a targoncához még nincs kép feltöltve.';            //  exit($_REQUEST['path']);            $variables = array(                "IMAGELIST" => $list,                "TRUCKID" => $_REQUEST['truckid'],                "SAXONID" => $saxonid,                "LASTIMAGE" => $truck[0]['truck_imagecount'],            );            $template->assign_var_array($variables);            $main_content = $template->compile("sys/lang/hun/truckman_imageeditor");  //WARNING: Language hardcoded, no other languages needed                include("sys/tpl/main.tpl");        }    }}?>