<?php    if (isauth())    {        $mysql = get_connection();                $SAXONID_LIST = '<option value="">' . $language['truck-details_saxon-id'] . '</option>';                $stmt = $mysql->prepare($sql['daily_offer:query_saxonID_list']);        if($stmt->execute())        {                        $result = $stmt->fetch_all();            foreach ($result as $row)            {                $SAXONID_LIST .= '<option value="' . $row['truck_id'] . '">' . $row['truck_saxon-id'] . '</option>';            }        }                $history = '';                    $stmt = $mysql->prepare($sql['daily_offer:query_list']);        if($stmt->execute())        {                        $result = $stmt->fetch_all();            foreach ($result as $row)            {                $history .= $row['truckID'] . ' @ ' . $row['date'] . '<br />';            }        }                    $template = new Template();        $variables = array ("SAXONID" => $language['truck-details_saxon-id'],                            "SAXONID_LIST" => $SAXONID_LIST,                            "HISTORY" => $history                            );        $template->assign_var_array($variables);        $main_content = $template->compile("sys/lang/" . $lang . "/admin_select_daily_special");        include("sys/tpl/main.tpl");    }?>