<?phpif (!loggedin() || !isauth()) {    redirect_in_site("?page=$default_page&lang=$lang");} else {    if ($_SESSION['users_type'] == 255) {        $filter = '';        $filters = array();        $filter__ = '';        if ($_REQUEST['companyName'] != '' && isset($_REQUEST['companyName']))            $filters[0] = '(SELECT `company_name` FROM `companies` WHERE `company_id` = (SELECT `users_company` FROM `users` WHERE `users`.`users_id` = `offer_requests`.`offer_user-id`)) LIKE \'%' . $_REQUEST['companyName'] . '%\'';        if ($_REQUEST['companyAddress'] != '' && isset($_REQUEST['companyAddress']))            $filters[1] = '(SELECT `company_address` FROM `companies` WHERE `company_id` = (SELECT `users_company` FROM `users` WHERE `users`.`users_id` = `offer_requests`.`offer_user-id`)) LIKE \'%' . $_REQUEST['companyAddress'] . '%\'';        if ($_REQUEST['offerID'] != '' && isset($_REQUEST['offerID']))            $filters[2] = '`offer_id` = \'' . $_REQUEST['offerID'] . '\'';        if ($_REQUEST['startdate'] != '' && isset($_REQUEST['startdate'])) {            $arr = split('-', $_REQUEST['startdate']);            $filters[3] = '`offer_date-added` > \'' . $arr[2] . '-' . $arr[1] . '-' . $arr[0] . '\'';        }        if ($_REQUEST['enddate'] != '' && isset($_REQUEST['enddate'])) {            $arr = split('-', $_REQUEST['enddate']);            $filters[4] = '`offer_date-added` < \'' . $arr[2] . '-' . $arr[1] . '-' . $arr[0] . '\'';        }        if ($_REQUEST['companyCountry'] != '' && isset($_REQUEST['companyCountry']))            $filters[1] = '(SELECT `company_country` FROM `companies` WHERE `company_id` = (SELECT `users_company` FROM `users` WHERE `users`.`users_id` = `offer_requests`.`offer_user-id`)) = ' . $_REQUEST['companyCountry'];        foreach ($filters as $f)            $filter .= (($filter != '') ? ' AND ' : '') . $f;        if ($filter == '') {            if ($_REQUEST['set'] == 'new')                $list = get_offer_request_list_clerk(-1);            else if ($_REQUEST['set'] == 'all')                $list = get_offer_request_list();            else if ($_REQUEST['set'] == 'mine')                $list = get_offer_request_list_clerk($_SESSION['users_id']);            else if ($_REQUEST['set'] == 'wip')                $list = get_offer_request_list_wip();            else if ($_REQUEST['set'] == 'closed')                $list = get_offer_request_list_status(4);            else                $list = get_offer_request_list_wip();        }        else            $list = get_offer_request_list_search($filter);        $menu = '<a href="?lang=' . $lang . '&page=offer_requests&set=all">' . $language['offer_request:show_all'] . '</a><br />' .//                '<a href="?lang='.$lang.'&page=offer_requests&set=new">' . $language['offer_request:show_new_only'] . '</a><br />' . //                '<a href="?lang='.$lang.'&page=offer_requests&set=mine">' . $language['offer_request:show_mine_only'] . '</a><br />' .                 '<a href="?lang=' . $lang . '&page=offer_requests&set=wip">' . $language['offer_request:show_wip_only'] . '</a><br />' .                '<a href="?lang=' . $lang . '&page=offer_requests&set=closed">' . $language['offer_request:show_closed_only'] . '</a><br /><br />';    }    else if ($_SESSION['users_type'] == 128) {        if ($_REQUEST['set'] == 'new')            $list = get_offer_request_list_clerk(-1);        else            $list = get_offer_request_list_clerk($_SESSION['users_id']);        $menu = '<a href="?lang=' . $lang . '&page=offer_requests&set=new">' . $language['offer_request:show_new_only'] . '</a><br />' .                '<a href="?lang=' . $lang . '&page=offer_requests&set=mine">' . $language['offer_request:show_mine_only'] . '</a><br /><br />';    }    // get company list    $mysql = get_connection();    $mysql->execute($sql['setutf']);    $stmt = $mysql->prepare($sql['countries:query_short_list']);    $countries = '<option value="" selected>Ã–sszes</option>';    if ($stmt->execute()) {        $result = $stmt->fetch_all();        foreach ($result as $row)            $countries .= '<option value="' . $row['country_id'] . (($_REQUEST['companyCountry'] == $row['country_id']) ? '">' : '">') . $row['country_name'] . '</option>';    }    $result = print_offer_set($list);    $template = new Template();    $variables = array(        "MENU" => $menu,        "COMPANYNAME" => $_REQUEST['companyName'],        "COMPANYADDRESS" => $_REQUEST['companyAddress'],        "OFFERID" => $_REQUEST['offerID'],        "OFFERREQUESTSLIST" => $result,        'COUNTRYLIST' => $countries    );    $template->assign_var_array($variables);    $main_content = $template->compile("sys/lang/hun/offer_requests");  //WARNING: Language hardcoded, no other languages needed    include("sys/tpl/main.tpl");}?>