<?php	/* * /  if(!loggedin() || !isauth())  {    redirect_in_site("?page=$default_page&lang=$lang");  }  else  {  /**/  	ob_start();						print '<h1 style="text-align:center;">Tárolási helyek nyomtatása</h1>';			print('				<script src="/js/hmtl-print.js"></script>				<iframe id="printf" name="printf" src="/blank.html" style="display:none;"></iframe>			');			$depot = Array(				1 => 'Vecsés 1',				2 => 'Vecsés 2',			);						$ldp = 0;			$mysql = get_connection();			$stmt = $mysql->prepare('				SELECT *				FROM `truck_location`				WHERE `code` != "" AND `subdepot`>0				ORDER BY `depot`, `code`			');			$stmt->execute();			$locations = $stmt->fetch_all();			print('<table cellspacing="0" cellpadding="3" style="min-width:320px;margin:auto;">');			foreach ($locations as $loc) {				if($ldp!=$loc['depot']) {					print('<tr><th colspan="3"><h2>'.($depot[$loc['depot']]).'</h2></th></tr>');					$ldp=$loc['depot'];				}				print('					<tr>						<td><strong>'.($loc['value']).'</strong></td>						<td><span>'.($loc['code']).'</span></td>						<td><img src="/img/qr.png" alt="" style="max-height:24px;vertical-align:baseline;cursor:pointer;" onclick="printLocationQR(`'.($depot[$loc['depot']]).' <br>'.($loc['value']).'`,`'.($loc['depot']).'`,`'.($loc['subdepot']).'`,`'.($loc['code']).'`);" /></td>					</tr>				');			}			print('</table>');			$main_content = ob_get_contents();		ob_end_clean();    include("sys/tpl/main.tpl");  // }?>